import turtle

t = turtle.Turtle()
# Tree top
t.penup() 
t.goto(565, -350)
t.pendown()
t.left(90)
t.pensize(4)
t.fillcolor("brown")
t.begin_fill()
for i in range(2):
    t.forward(350)
    t.right(90)
    t.forward(80)
    t.right(90)
t.end_fill()

t.penup() 
t.goto(565, -50)
t.pendown()

t.fillcolor("green")
t.begin_fill()
t.left(90)
t.forward(50)
t.right(120)
t.forward(170)
t.right(120)
t.forward(170)
t.right(120)
t.forward(50)
t.end_fill()

t.penup() 
t.goto(565, 30)
t.pendown()

t.fillcolor("green")
t.begin_fill()
t.forward(50)
t.right(120)
t.forward(170)
t.right(120)
t.forward(170)
t.right(120)
t.forward(50)
t.end_fill()

t.penup() 
t.goto(565, 110)
t.pendown()

t.fillcolor("green")
t.begin_fill()
t.forward(50)
t.right(120)
t.forward(170)
t.right(120)
t.forward(170)
t.right(120)
t.forward(50)
t.end_fill()
